import React, { useState, useEffect } from 'react';
import './style.css';

function Header() {
  const [activeSection, setActiveSection] = useState("home");

  const handleSectionClick = (section) => {
    setActiveSection(section);
  };

  useEffect(() => {
    const handleScroll = () => {
      const scrollTop = window.pageYOffset || document.documentElement.scrollTop || document.body.scrollTop || 0;
      
      if (scrollTop >= 0 && scrollTop >= 0) {
        setActiveSection("home");
      } else if (scrollTop >= 1400) {
        setActiveSection("work");
      }
    };

    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <React.Fragment>
      <div className='header_opacity_container'>
        <div>
          <h2>Ultra Profile</h2>
          <ul>
            <li>
              <a
                onClick={() => handleSectionClick("home")}
                style={{ color: activeSection === "home" ? "#eb5424" : "black" }}
                href="#home_header"
              >
                HOME
              </a>
            </li>
            <li>
              <a
                onClick={() => handleSectionClick("work")}
                style={{ color: activeSection === "work" ? "#eb5424" : "black" }}
                href="#work_container"
              >
                MY WORK
              </a>
            </li>
            <li>
              <a
                onClick={() => handleSectionClick("portfolio")}
                style={{ color: activeSection === "portfolio" ? "#eb5424" : "black" }}
                href="#port_id"
              >
                PORTFOLIO
              </a>
            </li>
            <li>
              <a
                onClick={() => handleSectionClick("resume")}
                style={{ color: activeSection === "resume" ? "#eb5424" : "black" }}
                href="#ressume_id"
              >
                RESUME
              </a>
            </li>
            <li>
              <a
                onClick={() => handleSectionClick("about")}
                style={{ color: activeSection === "about" ? "#eb5424" : "black" }}
                href="#about_id"
              >
                ABOUT
              </a>
            </li>
            <li>
              <a
                onClick={() => handleSectionClick("contact")}
                style={{ color: activeSection === "contact" ? "#eb5424" : "black" }}
                href="#contact"
              >
                CONTACT
              </a>            </li>
          </ul>
        </div>
      </div>
      <div className='home_container' id='home_header'>
        <h2>HUSEYN EYVAZOV</h2>
        <h3>Front-End Developer</h3>
        <p>
          I am a professional <b>UX Designer</b> and <b>Front-End Developer</b> creating modern and responsive designs for Web and Mobile. Let us work together. Thank you.
        </p>
        <button>Let's Begin</button>
      </div>
    </React.Fragment>
  );
}

export default Header;
