const handleAbout=()=>{
setAboutColor("#eb5424");
setHomeColor("black");
SetResumeColor("black");
setWorkColor("black");
SetPortColor("black");
setContactColor("black");
}
const handleContact=()=>{
  setContactColor("#eb5424");
