    setAboutColor("black");
}


const handleHome=()=>{
    setHomeColor("rgb(235, 84, 36)");
    setWorkColor("black");
    SetResumeColor("black");
    SetPortColor("black");
    setContactColor("black");
    setAboutColor("black");
}


const handleResume=()=>{
SetResumeColor("rgb(235, 84, 36)");
setWorkColor("black");
setHomeColor("black");
SetPortColor("black");
setContactColor("black");
setAboutColor("black");
}


document.getElementById("body").onscroll=()=>{
  console.log(window.pageYOffset);
  
  if(window.pageYOffset>=0 && window.pageYOffset<=710){
setHomeColor("#eb5424");
setWorkColor("black");
SetPortColor("black");
setContactColor("black");
setAboutColor("black");
