setWorkColor("#eb5424");
setHomeColor("black");
SetPortColor("black");
setContactColor("black");
setAboutColor("black");
SetResumeColor("black");
  }else if(window.pageYOffset>=1520 && window.pageYOffset<=2230){
SetPortColor("#eb5424");
setWorkColor("black");
setHomeColor("black");
setContactColor("black");
setAboutColor("black");
SetResumeColor("black");
}else if(window.pageYOffset>=2240 && window.pageYOffset<=2900){
SetResumeColor("#eb5424");
 SetPortColor("black");
setWorkColor("black");
setHomeColor("black");
setContactColor("black");
setAboutColor("black");
}else if(window.pageYOffset>=2990){
setAboutColor("#eb5424");
SetResumeColor("black");
SetPortColor("black");
setWorkColor("black");
setHomeColor("black");
setContactColor("black");

}
}
