      setHomeColor("black");
      setContactColor("black");
      
      }
  }
}

const handleBeginClick = () => {
  const workContainer = document.getElementById('work_container');
  if (workContainer) {
    handleWork();
    window.scrollTo({
      top: workContainer.offsetTop,
      behavior: 'smooth',
    } 
    );
  }
};
