      <React.Fragment>
        <div className='header_opacity_container'>
          <div>
            <h2>Front-End Developer</h2>
            <ul>
              <li> <a onClick={handleHome} id='home_li' style={{color:homeColor}} href="#home_header">HOME</a></li>
              <li> <a onClick={handleWork} id='work_li' href="#work_container" style={{color:workColor}} >MY WORK</a></li>
              <li><a onClick={handlePort} id='port_li'style={{color:portColor}} href="#port_id">PORTFOLIO</a></li>
              <li><a onClick={handleResume} id='ressume_li' href="#ressume_id" style={{color:resumeColor}}>RESUME</a></li>
              <li><a onClick={handleAbout} id='about_li' style={{color:aboutColor}} href="#about_id">ABOUT</a></li>
            </ul>
          </div>
        </div>
        <div className='home_container' id='home_header'>
          <h2>HUSEYN EYVAZOV</h2>
          <h3>Front-End Developer</h3>
          <p>I am a professional <b>UX Designer</b>  and <b>Front-End Developer</b>  creating modern and responsive designs for Web and Mobile. Let us work together. Thank you.</p>
          <button onClick={handleBeginClick}>Let's Begin</button>
        </div>
      </React.Fragment>
    )
  }
  
  export default Header;
