import React, { useState } from 'react';
import './style.css';


function Header() {
const[workColor,setWorkColor]=useState("");
const[homeColor,setHomeColor]=useState("");
const[resumeColor,SetResumeColor]=useState("");
const[portColor,SetPortColor]=useState("");
const[aboutColor,setAboutColor]=useState("");
const[contactColor,setContactColor]=useState("");


const handleAbout=()=>{
setAboutColor("#eb5424");
setHomeColor("black");
SetResumeColor("black");
setWorkColor("black");
SetPortColor("black");
setContactColor("black");
}
const handleContact=()=>{
  setContactColor("#eb5424");
  setHomeColor("black");
  SetResumeColor("black");
  setWorkColor("black");
  SetPortColor("black");
  setAboutColor("black");
}
const handlePort=()=>{ 
  SetPortColor("#eb5424");
  setHomeColor("black");
  SetResumeColor("black");
  setWorkColor("black");
  setContactColor("black");
  setAboutColor("black");
 
}
const handleWork=()=>{
    setWorkColor("rgb(235, 84, 36)");
    setHomeColor("black");
    SetResumeColor("black");
    SetPortColor("black");
    setContactColor("black");
    setAboutColor("black");
}
const handleHome=()=>{
    setHomeColor("rgb(235, 84, 36)");
    setWorkColor("black");
    SetResumeColor("black");
    SetPortColor("black");
    setContactColor("black");
    setAboutColor("black");
}
const handleResume=()=>{
SetResumeColor("rgb(235, 84, 36)");
setWorkColor("black");
setHomeColor("black");
SetPortColor("black");
setContactColor("black");
setAboutColor("black");
}

const BeginOnclik=()=>{
  window.location.href("#work_container")
}

 return (
      <React.Fragment>
        <div className='header_opacity_container'>
          <div>
            <h2>Front-End Developer</h2>
            <ul>
              <li> <a onClick={handleHome} id='home_li' style={{color:homeColor}} href="#home_header">HOME</a></li>
              <li> <a onClick={handleWork} id='work_li' href="#work_container" style={{color:workColor}} >MY WORK</a></li>
              <li><a onClick={handlePort} id='port_li'style={{color:portColor}} href="#port_id">PORTFOLIO</a></li>
              <li><a onClick={handleResume} id='ressume_li' href="#ressume_id" style={{color:resumeColor}}>RESUME</a></li>
              <li><a onClick={handleAbout} id='about_li' style={{color:aboutColor}} href="#about_id">ABOUT</a></li>
            </ul>
          </div>
        </div>
        <div className='home_container' id='home_header'>
          <h2>HUSEYN EYVAZOV</h2>
          <h3>Front-End Developer</h3>
          <p>I am a professional <b>UX Designer</b>  and <b>Front-End Developer</b>  creating modern and responsive designs for Web and Mobile. Let us work together. Thank you.</p>
          <button onClick={BeginOnclik}>Let's Begin</button>
        </div>
      </React.Fragment>
    )
  }
  
  export default Header;
