import React, { useState } from 'react';
import './style.css';


function Header() {
const[workColor,setWorkColor]=useState("");
const[homeColor,setHomeColor]=useState("");
const[resumeColor,SetResumeColor]=useState("");

const handleWork=()=>{
    setWorkColor("rgb(235, 84, 36)");
    setHomeColor("black");
    SetResumeColor("black");
}
const handleHome=()=>{
    setHomeColor("rgb(235, 84, 36)");
    setWorkColor("black");
    SetResumeColor("black");
}
const handleResume=()=>{
SetResumeColor("rgb(235, 84, 36)");
setWorkColor("black");
setHomeColor("black");
}
const fixAllColor=()=>{
  document.getElementById("home_li").style.color="black"
  document.getElementById("work_li").style.color="black"
  document.getElementById("contact_li").style.color="black"
  document.getElementById("ressume_li").style.color="black"
  document.getElementById("port_li").style.color="black"
  document.getElementById("about_li").style.color="black"
}
document.getElementById("body").onscroll=()=>{
  console.log(window.pageYOffset);
  if(window.pageYOffset>=700){
    fixAllColor();
    document.getElementById("work_li").style.color="#eb5424"
  } else if(window.pageYOffset){}
}


 return (
      <React.Fragment>
        <div className='header_opacity_container'>
          <div>
            <h2>Ultra Profile</h2>
            <ul>
              <li> <a onClick={handleHome} id='home_li' style={{color:homeColor}} href="#home_header">HOME</a></li>
              <li> <a onClick={handleWork} id='work_li' href="#work_container" style={{color:workColor}} >MY WORK</a></li>
              <li><a id='port_li' href="#portfolio_id">PORTFOLIO</a></li>
              <li><a onClick={handleResume} id='ressume_li' href="#ressume_id" style={{color:resumeColor}}>RESUME</a></li>
              <li><a id='about_li' href="">ABOUT</a></li>
              <li><a id='contact_li' href="">CONTACT</a></li>
            </ul>
          </div>
        </div>
        <div className='home_container' id='home_header'>
          <h2>HUSEYN EYVAZOV</h2>
          <h3>Front-End Developer</h3>
          <p>I am a professional <b>UX Designer</b>  and <b>Front-End Developer</b>  creating modern and responsive designs for Web and Mobile. Let us work together. Thank you.</p>
          <button>Let's Begin</button>
        </div>
      </React.Fragment>
    )
  }
  
  export default Header;
