import React, { useState, useEffect } from 'react';
import './style.css';

function Header() {
  const [activeLink, setActiveLink] = useState('home');

  useEffect(() => {
    const handleScroll = () => {
      const scrollY = window.pageYOffset || document.documentElement.scrollTop;
      
      if (scrollY >= 0 && scrollY <= 710) {
        setActiveLink('home');
      } else if (scrollY >= 730 && scrollY <= 1500) {
        setActiveLink('work');
      } else if (scrollY >= 1520 && scrollY <= 2230) {
        setActiveLink('portfolio');
      } else if (scrollY >= 2240 && scrollY <= 2900) {
        setActiveLink('resume');
      } else if (scrollY >= 2990) {
        setActiveLink('about');
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const handleClick = (link) => {
    setActiveLink(link);

    const element = document.getElementById(link);
    if (element) {
      window.scrollTo({
        top: element.offsetTop,
        behavior: 'smooth',
      });
    }
  };

  return (
    <React.Fragment>
      <div className="header_opacity_container">
        <div>
          <h2>Front-End Developer</h2>
          <ul>
            <li>
              <a
                onClick={() => handleClick('home')}
                style={{ color: activeLink === 'home' ? '#eb5424' : 'black' }}
                href="#home_header"
              >
                HOME
              </a>
            </li>
            <li>
              <a
                onClick={() => handleClick('work')}
                style={{ color: activeLink === 'work' ? '#eb5424' : 'black' }}
                href="#work_container"
              >
                MY WORK
              </a>
            </li>
            <li>
              <a
                onClick={() => handleClick('portfolio')}
                style={{ color: activeLink === 'portfolio' ? '#eb5424' : 'black' }}
                href="#port_id"
              >
                PORTFOLIO
              </a>
            </li>
            <li>
              <a
                onClick={() => handleClick('resume')}
                style={{ color: activeLink === 'resume' ? '#eb5424' : 'black' }}
                href="#resume_id"
              >
                RESUME
              </a>
            </li>
            <li>
              <a
                onClick={() => handleClick('about')}
                style={{ color: activeLink === 'about' ? '#eb5424' : 'black' }}
                href="#about_id"
              >
                ABOUT
              </a>
            </li>
          </ul>
        </div>
      </div>
      <div className="home_container" id="home_header">
        <h2>HUSEYN EYVAZOV</h2>
        <h3>Front-End Developer</h3>
        <p>
          I am a professional <b>UX Designer</b> and <b>Front-End Developer</b> creating modern and responsive designs for Web and Mobile. Let us work together. Thank you.
        </p>
        <button onClick={() => handleClick('work')}>Let's Begin</button>
      </div>
    </React.Fragment>
  );
}

export default Header;
